import java.util.HashMap;
import java.util.Map;

public class MinWindow {

    public void putIntoNeedMap(Map<Character, Integer> map, char c) {
        if(map.containsKey(c)) {
            int num = map.get(c);
            map.put(c, ++num);
        } else {
            map.put(c, 1);
        }
    }

    public void putIntoHaveMap(Map<Character, Integer> map, char c) {
        if (map.containsKey((c))) {
            int num = map.get(c);
            map.put(c, ++num);
        } else {
            map.put(c, 1);
        }
    }

    public String minWindow(String s, String t) {
        Map<Character, Integer> tMap = new HashMap<>();
        Map<Character, Integer> hMap = new HashMap<>();
        Map<Integer, String> fMap = new HashMap<>();

        int haveCount = 0, needCount = 0, minWindow = Integer.MAX_VALUE;
        for (int i = 0; i < t.length(); i++) {
            char c = t.charAt(i);
            putIntoNeedMap(tMap, c);
            needCount++;
        }

        for (int i = 0, j = 0; j < s.length(); j++) {
            char c = s.charAt(j);
            if (tMap.containsKey(c)) {
                putIntoHaveMap(hMap, c);
                if(hMap.get(c) <= tMap.get(c)) {
                    haveCount++;
                }

                if (haveCount == needCount) {
                    if((j - i) < minWindow) {
                        fMap.put(j - i, s.substring(i, (j + 1)));
                        minWindow = j - i;
                    }

                    for(int k = i; ; k++) {
                        char ch = s.charAt(k);
                        if (hMap.containsKey(ch)) {
                            int num = hMap.get(ch);
                            hMap.put(ch, --num);
                            if(tMap.get(ch) > hMap.get(ch)) {
                                haveCount--;
                                if (haveCount != needCount) {
                                    do {
                                        if(++k < s.length()) {
                                            ch = s.charAt(k);
                                        }
                                    } while(!hMap.containsKey(ch));
                                    i = k;
                                    break;
                                }
                            }
                        }
                    }
                }
            }
        }

        if (fMap.isEmpty()) {
            return new String("");
        } else {
            return fMap.get(minWindow);
        }
    }
    public void run() {
        String s = "";
        String t = "";
        System.out.println(minWindow("a", "aa"));
        System.out.println(minWindow("ADOBECODEBANC", "ABC"));
        System.out.println(minWindow("a", "a"));
        System.out.println(minWindow("abc", "cba"));
        System.out.println(minWindow("aaaaaaaaaaaabbbbbcdd", "abcdd"));
        System.out.println(minWindow("aabbbcdd", "abcdd"));
        System.out.println(minWindow("ab", "a"));
        System.out.println(minWindow(s, t));
    }

    public static void main(String[] args) {
        MinWindow obj = new MinWindow();
        obj.run();
    }
}
